mapper.select:
SELECT * FROM ( SELECT eb.*,EXHIBITION_CATEGORY, pdct.*, ROW_NUMBER() OVER (PARTITION BY ex.exhibition_id ORDER BY pd.product_display_id) as row_num FROM exhibition_banner eb JOIN exhibition ex ON ex.exhibition_id = eb.exhibition_id JOIN exhibition_category ec ON ec.exhibition_id = ex.exhibition_id JOIN exhibition_connect econn ON econn.exhibition_category_id = ec.exhibition_category_id JOIN product_display pd ON pd.product_display_id = econn.product_display_id JOIN (SELECT MAX(product_display_id) as product_display_id, category_small_id FROM product GROUP BY product_display_id, category_small_id) p ON p.product_display_id = pd.product_display_id JOIN category_small cs ON cs.category_small_id = p.category_small_id JOIN category_mid cm ON cm.category_mid_id = cs.category_mid_id JOIN category_large cl ON cl.category_large_id = cm.category_large_id
and cl.category_large_id = #{categoryLargeId}
JOIN productcontainer pdct ON pd.product_display_id = pdct.product_display_id WHERE EXHIBITION_END_DAY > SYSDATE and EXHIBITION_CATEGORY = 10 ) WHERE row_num<=2
